<?phpif (class_exists ( 'WPBakeryShortCode' )) {	class WPBakeryShortCode_wander_countdown_timer extends WPBakeryShortCode {				/*		 * This methods returns HTML code for frontend representation of your shortcode.		 * You can use your own html markup.		 *		 * @param $atts - shortcode attributes		 * @param @content - shortcode content		 *		 * @access protected		 * vc_filter: VC_SHORTCODE_CUSTOM_CSS_FILTER_TAG vc_shortcodes_css_class - hook to edit element class		 * @return string		 */		protected function content($atts, $content = null) {			extract ( shortcode_atts ( array (					'date' => '',					'counter_size' => '',			), $atts ) );			$output = '							<div data-date="'.esc_html($date).'" id="countdown-timer" class="'.$counter_size.'">					<div><h1 id="months"></h1><h6>Months</h6></div> 					<div><h1 id="days"></h1><h6>Days</h6></div> 					<div><h1 id="hours"></h1><h6>Hours</h6></div> 					<div><h1 id="minutes"></h1><h6>Minutes</h6></div> 					<div><h1 id="seconds"></h1><h6>Seconds</h6></div>				</div>';							return $output;		}	}}