<?phpif (class_exists ( 'WPBakeryShortCode' )) {	class WPBakeryShortCode_wander_counter extends WPBakeryShortCode {				/*		 * This methods returns HTML code for frontend representation of your shortcode.		 * You can use your own html markup.		 *		 * @param $atts - shortcode attributes		 * @param @content - shortcode content		 *		 * @access protected		 * vc_filter: VC_SHORTCODE_CUSTOM_CSS_FILTER_TAG vc_shortcodes_css_class - hook to edit element class		 * @return string		 */		protected function content($atts, $content = null) {			extract ( shortcode_atts ( array (					'item_name' => '',					'item_upto' => ''			), $atts ) );			$output = '				<div class="counter">					<h1>'.esc_html($item_upto).'</h1>                     <h5>'.esc_html($item_name).'</h5>				</div>';			return $output;		}	}}