<?phpif (class_exists ( 'WPBakeryShortCode' )) {	class WPBakeryShortCode_wander_pricing_column extends WPBakeryShortCode {		/*		 * This methods returns HTML code for frontend representation of your shortcode.		 * You can use your own html markup.		 *		 * @param $atts - shortcode attributes		 * @param @content - shortcode content		 *		 * @access protected		 * vc_filter: VC_SHORTCODE_CUSTOM_CSS_FILTER_TAG vc_shortcodes_css_class - hook to edit element class		 * @return string		 */		protected function content($atts, $content = null) {			extract(shortcode_atts(			array(				'plan_name'       => '',								'price'       => '',				'title'       => '',				'per'        => '',								'button_link'        => '',								'button_text'        => '',								'featured'        => '',								'is_paid'        => ''			), $atts)		);		$includes = '';		$output = '';		if (isset($content) && !empty($content)) {			$includes .='<div class="price-features">';				$split = preg_split("/(\r?\n)+|(<br\s*\/?>\s*)+/", $content);				$i = 1;				foreach($split as $haystack) {					if($i==1){						$includes .= '<h5>' . $haystack . '</h5>';					}else{						$includes .= '<p>' . $haystack . '</p>';					}					$i++;				}							$includes .='</div>';		}					if($featured){					$output .='	<div class="price-table-featured">';				}else{					$output .='	<div class="price-table">';					}		       		$output .='	<div class="price-box">                             <h4>'.esc_html($plan_name).'</h4>';										if($is_paid){								$output .='	<h2 class="price-price">                                 <sup>$</sup>                                <span>'.esc_html($price);																if($per != null && $per !=""){$output .='<span>/ '.$per.'</span>';}																								$output .='</span>                             </h2>';										}else{							$output .=	'<h2 class="price-price">                                  <span>FREE</span>                             </h2>';							}																									$output .= 	$includes.'                            <a href="'.esc_url($button_link).'" class="btn btn-primary btn-md btn-appear"><span>'.esc_html($button_text).' <i class="ion-android-arrow-forward"></i></span></a>                        </div>                    </div>';		return $output;		}	}}